# coding: utf-8


class Solution:
    def match(self, part, words, word_len):
        tmp_words = list(words)
        match_len = len(part) // word_len
        for i in range(match_len):
            word = part[i * word_len: (i + 1) * word_len]
            if word in tmp_words:
                tmp_words.remove(word)
            else:
                return False
        return True

    def findSubstring(self, s: str, words: 'List[str]') -> 'List[int]':
        if (not s) or (not words):
            return list()
        word_len = len(words[0])
        part_len = word_len * len(words)
        ans = list()
        for i in range(len(s) - word_len * len(words) + 1):
            if self.match(s[i: i + part_len], words, word_len):
                ans.append(i)
        return ans


if __name__ == '__main__':
    s = Solution()
    print(s.findSubstring("barfoothefoobarman", ["foo","bar"]))
    print(s.findSubstring("wordgoodgoodgoodbestword", ["word","good","best","word"]))
    print(s.findSubstring("foobarfoobar", ["foo", "bar"]))
    print(s.findSubstring("wordgoodgoodgoodbestword", ["word","good","best","good"]))
    print(s.findSubstring("", []))
    print(s.findSubstring("a", []))
    print(s.findSubstring("", ["dhvf","sind","ffsl","yekr","zwzq","kpeo","cila","tfty","modg","ztjg","ybty","heqg","cpwo","gdcj","lnle","sefg","vimw","bxcb"]))
